    public static ArrayList<Integer> solve(ArrayList<Integer> A, int B) {
        ArrayList<Integer> res = new ArrayList<>();
        res.add(left(A,B));
        res.add(right(A,B));

        return res;
    }
    public static int left(ArrayList<Integer> A, int B) {
        int l = 0; int r = A.size()-1; int mid;
        while(l <= r) {
            mid = l + (r - l)/2;
            if(A.get(mid) == B && ((mid == 0) || !Objects.equals(A.get(mid), A.get(mid - 1)))) return mid;
            if(A.get(mid) < B) l = mid + 1;
            else r = mid - 1;
        }
        return -1;
    }
    public static int right(ArrayList<Integer> A,int B) {
        int l = 0; int r = A.size()-1; int mid;
        while(l <= r) {
            mid = l + (r - l)/2;
            if(A.get(mid) == B && ((mid == A.size()-1) || !Objects.equals(A.get(mid), A.get(mid + 1)))) return mid;
            if(A.get(mid) > B) r = mid - 1;
            else l = mid + 1;
        }
        return -1;
    }
